import React from 'react';
import { Link } from 'react-router-dom';
import { motion, useScroll, useTransform } from 'framer-motion';
import {
  Shield,
  HardHat,
  Target,
  CheckCircle,
  Users,
  TrendingUp,
  Globe,
  ArrowRight,
  Award,
  MapPin
} from 'lucide-react';
import { openWhatsAppQuote } from '../utils/whatsapp';
import HeroSlider from '../components/HeroSlider';

const Home: React.FC = () => {
  const { scrollY } = useScroll();
  const y = useTransform(scrollY, [0, 300], [0, -50]);
  const opacity = useTransform(scrollY, [0, 300], [1, 0.8]);
  const scale = useTransform(scrollY, [0, 300], [1, 0.95]);

  // Hero slider images
  const heroImages = [
    '/images/hero/slider-1.jpg',
    '/images/hero/slider-2.jpg',
    '/images/hero/slider-3.jpg'
  ];

  const features = [
    {
      icon: <Shield className="h-12 w-12 text-primary-600" />,
      title: "Sleek and Aesthetic Design",
      description: "These grills provide a modern, invisible look, blending seamlessly with your balcony or window"
    },
    {
      icon: <HardHat className="h-12 w-12 text-primary-600" />,
      title: "Durable and Strong",
      description: "Made from high-quality, rust-resistant materials like stainless steel, offering long-lasting strength"
    },
    {
      icon: <Award className="h-12 w-12 text-accent-600" />,
      title: "Enhanced Safety",
      description: "Effectively prevent falls, providing a safe environment for children, pets, and residents"
    },
    {
      icon: <CheckCircle className="h-12 w-12 text-secondary-600" />,
      title: "Quality Guarantee",
      description: "ISO certified processes and materials with superior craftsmanship"
    }
  ];

  const stats = [
    { number: "5200+", label: "Projects Done", icon: <CheckCircle className="h-6 w-6" /> },
    { number: "5100+", label: "Happy Clients", icon: <Users className="h-6 w-6" /> },
    { number: "40+", label: "Expert Staffs", icon: <TrendingUp className="h-6 w-6" /> },
    { number: "25+", label: "Years Experience", icon: <Globe className="h-6 w-6" /> }
  ];

  return (
    <div className="pt-16">
      {/* Hero Section with Slider Background */}
      <motion.section 
        className="relative bg-gradient-to-br from-primary-900 via-primary-800 to-primary-900 text-white min-h-screen flex items-center overflow-hidden"
      >
            {/* Hero Slider Background */}
            <HeroSlider images={heroImages} interval={3000} />
        
        {/* Strong Black Overlay for Text Readability */}
        <div className="absolute inset-0 bg-gradient-to-br from-black/70 via-gray-900/60 to-black/70"></div>
        
        {/* Static Safety Elements - Optimized */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          <div className="absolute top-20 left-10 opacity-20">
            <Shield className="h-16 w-16 text-accent-400" />
          </div>
          
          <div className="absolute top-40 right-20 opacity-15">
            <HardHat className="h-12 w-12 text-accent-300" />
          </div>
          
          <div className="absolute bottom-40 left-20 opacity-10">
            <Target className="h-14 w-14 text-secondary-300" />
          </div>
          
          <div className="absolute bottom-20 right-10 opacity-20">
            <CheckCircle className="h-10 w-10 text-secondary-400" />
          </div>
        </div>
        
        
        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-24 lg:py-32">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <motion.div
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
              whileInView={{ 
                opacity: 1,
                x: 0,
                transition: { duration: 0.8 }
              }}
            >
                  <motion.h1 
                    className="text-4xl sm:text-5xl md:text-6xl lg:text-6xl font-bold leading-tight mb-8 text-white"
                    style={{ 
                      textShadow: '2px 2px 8px rgba(0,0,0,0.3)'
                    }}
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 0.2 }}
                    whileInView={{ 
                      opacity: 1,
                      y: 0,
                      transition: { duration: 0.8 }
                    }}
                  >
                Invisible Grills & 
                  <span className="text-accent-400 font-semibold"> Cloth Hangers</span>
              </motion.h1>
              
              {/* Company Tagline with Moving Background Effect */}
              <div className="relative mb-6">
                    <motion.div
                      className="text-2xl sm:text-3xl md:text-4xl lg:text-4xl font-semibold text-accent-400 mb-4"
                      style={{
                        textShadow: '1px 1px 3px rgba(0,0,0,0.3)'
                      }}
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ duration: 0.8, delay: 0.3 }}
                      whileInView={{ 
                        opacity: 1,
                        y: 0,
                        transition: { duration: 0.8 }
                      }}
                    >
                  THE LEADING INVISIBLE GRILL MANUFACTURERS AND INSTALLERS
                </motion.div>
                <motion.div
                  className="text-lg lg:text-xl text-gray-200 mt-2"
                  style={{
                    textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
                  }}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.8, delay: 0.5 }}
                  whileInView={{ 
                    opacity: 1,
                    y: 0,
                    transition: { duration: 0.8 }
                  }}
                >
                  Professional Invisible Grill Installation & Cloth Hanger Solutions
                </motion.div>
              </div>
              
              
              <motion.div 
                className="flex flex-col sm:flex-row gap-4 justify-center"
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.9 }}
                whileInView={{ 
                  opacity: 1,
                  y: 0,
                  transition: { duration: 0.8 }
                }}
              >
                    <button
                      onClick={openWhatsAppQuote}
                      className="bg-accent-600 hover:bg-accent-700 text-white px-6 sm:px-8 py-4 rounded-lg font-semibold text-base sm:text-lg transition-all duration-300 inline-flex items-center justify-center cursor-pointer shadow-lg hover:shadow-xl w-full sm:w-auto"
                    >
                      Get Free Quote
                      <ArrowRight className="ml-2 h-5 w-5 sm:h-6 sm:w-6" />
                    </button>
                
                    <Link
                      to="/services"
                      className="bg-secondary-600 hover:bg-secondary-700 text-white px-6 sm:px-8 py-4 rounded-lg font-semibold text-base sm:text-lg transition-all duration-300 inline-flex items-center justify-center shadow-lg hover:shadow-xl border-2 border-secondary-500 hover:border-secondary-600 w-full sm:w-auto"
                    >
                      <Shield className="mr-2 h-5 w-5 sm:h-6 sm:w-6" />
                      View Our Services
                    </Link>
                    
                    <a
                      href="/about#locations"
                      className="bg-primary-700 hover:bg-primary-800 text-white px-6 sm:px-8 py-4 rounded-lg font-semibold text-base sm:text-lg transition-all duration-300 inline-flex items-center justify-center shadow-lg hover:shadow-xl border-2 border-primary-600 hover:border-primary-700 cursor-pointer w-full sm:w-auto"
                    >
                      <MapPin className="mr-2 h-5 w-5 sm:h-6 sm:w-6" />
                      View Locations
                    </a>
              </motion.div>
            </motion.div>
            <motion.div
              initial={{ opacity: 0, x: 50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              className="text-center"
            >
            </motion.div>
          </div>
        </div>
      </motion.section>

          {/* Invisible Grills Applications Showcase */}
          <motion.section 
            className="py-20 bg-gradient-to-br from-primary-50 to-secondary-50"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div 
            className="text-center mb-16"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <motion.h2 
              className="text-3xl lg:text-4xl font-bold text-gray-900 mb-4"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              Our Premium Invisible Grill Solutions
            </motion.h2>
            <motion.p 
              className="text-xl text-gray-600 max-w-3xl mx-auto"
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              Transform your home with our premium invisible grills and cloth hangers - perfect for balconies, windows, apartments, and modern living spaces
            </motion.p>
          </motion.div>
          
          <div className="grid grid-cols-2 md:grid-cols-2 lg:grid-cols-4 gap-3 sm:gap-4 md:gap-6 lg:gap-8">
            {/* Invisible Grill for Balconies */}
            <Link to="/services/invisible-grill-for-balconies">
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
                className="bg-gradient-to-br from-white to-primary-50 rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-all duration-300 group cursor-pointer"
              >
                <div className="h-32 sm:h-40 md:h-48 bg-cover bg-center bg-no-repeat relative overflow-hidden">
                  {/* Invisible Grill for Balconies Image */}
                  <div className="absolute inset-0 bg-cover bg-center bg-no-repeat" style={{
                    backgroundImage: `url('/images/safety-nets/invisible-grill-balconies.jpg')`
                  }}></div>
                  
                  {/* Dark Overlay for better text visibility */}
                  <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-black/50 to-black/40"></div>
                  
                  <div className="text-center text-white relative z-10 h-full flex flex-col items-center justify-center px-2">
                    <h3 className="text-sm sm:text-base md:text-xl font-semibold drop-shadow-lg group-hover:scale-110 transition-transform duration-300" style={{textShadow: '2px 2px 4px rgba(0,0,0,0.8)'}}>Balcony Grills</h3>
                    <p className="text-xs sm:text-sm opacity-90 drop-shadow-lg" style={{textShadow: '2px 2px 4px rgba(0,0,0,0.8)'}}>Premium invisible</p>
                  </div>
                </div>
              </motion.div>
            </Link>

            {/* Invisible Grill for Windows */}
            <Link to="/services/invisible-grill-for-windows">
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.1 }}
                className="bg-gradient-to-br from-white to-primary-50 rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-all duration-300 group cursor-pointer"
              >
                <div className="h-32 sm:h-40 md:h-48 bg-cover bg-center bg-no-repeat relative overflow-hidden">
                  {/* Invisible Grill for Windows Image */}
                  <div className="absolute inset-0 bg-cover bg-center bg-no-repeat" style={{
                    backgroundImage: `url('/images/safety-nets/invisible-grill-windows.jpg')`,
                    backgroundPosition: 'center 30%'
                  }}></div>
                  
                  {/* Dark Overlay for better text visibility */}
                  <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-black/50 to-black/40"></div>
                  
                  <div className="text-center text-white relative z-10 h-full flex flex-col items-center justify-center px-2">
                    <h3 className="text-sm sm:text-base md:text-xl font-semibold drop-shadow-lg group-hover:scale-110 transition-transform duration-300" style={{textShadow: '2px 2px 4px rgba(0,0,0,0.8)'}}>Window Grills</h3>
                    <p className="text-xs sm:text-sm opacity-90 drop-shadow-lg" style={{textShadow: '2px 2px 4px rgba(0,0,0,0.8)'}}>Elegant security</p>
                  </div>
                </div>
              </motion.div>
            </Link>

            {/* Pull & Dry Cloth Hangers */}
            <Link to="/services/pull-dry-cloth-hangers">
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.2 }}
                className="bg-gradient-to-br from-white to-primary-50 rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-all duration-300 group cursor-pointer"
              >
                <div className="h-32 sm:h-40 md:h-48 bg-cover bg-center bg-no-repeat relative overflow-hidden">
                  {/* Pull & Dry Cloth Hangers Image */}
                  <div className="absolute inset-0 bg-cover bg-center bg-no-repeat" style={{
                    backgroundImage: `url('/images/safety-nets/pull-dry-cloth-hangers.jpg')`
                  }}></div>
                  
                  {/* Dark Overlay for better text visibility */}
                  <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-black/50 to-black/40"></div>
                  
                  <div className="text-center text-white relative z-10 h-full flex flex-col items-center justify-center px-2">
                    <h3 className="text-sm sm:text-base md:text-xl font-semibold drop-shadow-lg group-hover:scale-110 transition-transform duration-300" style={{textShadow: '2px 2px 4px rgba(0,0,0,0.8)'}}>Pull & Dry Hangers</h3>
                    <p className="text-xs sm:text-sm opacity-90 drop-shadow-lg" style={{textShadow: '2px 2px 4px rgba(0,0,0,0.8)'}}>Space-saving</p>
                  </div>
                </div>
              </motion.div>
            </Link>

            {/* Ceiling Cloth Hangers */}
            <Link to="/services/ceiling-cloth-hangers">
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.3 }}
                className="bg-gradient-to-br from-white to-primary-50 rounded-xl overflow-hidden shadow-lg hover:shadow-xl transition-all duration-300 group cursor-pointer"
              >
                <div className="h-32 sm:h-40 md:h-48 bg-cover bg-center bg-no-repeat relative overflow-hidden">
                  {/* Ceiling Cloth Hangers Image */}
                  <div className="absolute inset-0 bg-cover bg-center bg-no-repeat" style={{
                    backgroundImage: `url('/images/safety-nets/ceiling-cloth-hangers.jpg')`
                  }}></div>
                  
                  {/* Dark Overlay for better text visibility */}
                  <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-black/50 to-black/40"></div>
                  
                  <div className="text-center text-white relative z-10 h-full flex flex-col items-center justify-center px-2">
                    <h3 className="text-sm sm:text-base md:text-xl font-semibold drop-shadow-lg group-hover:scale-110 transition-transform duration-300" style={{textShadow: '2px 2px 4px rgba(0,0,0,0.8)'}}>Ceiling Hangers</h3>
                    <p className="text-xs sm:text-sm opacity-90 drop-shadow-lg" style={{textShadow: '2px 2px 4px rgba(0,0,0,0.8)'}}>Ceiling-mounted</p>
                  </div>
                </div>
              </motion.div>
            </Link>

          </div>
          
          {/* View More Services Button */}
          <motion.div 
            className="text-center mt-12"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <Link
              to="/services"
              className="inline-flex items-center bg-accent-600 hover:bg-accent-700 text-white px-8 py-4 rounded-lg font-semibold text-lg transition-all duration-300 shadow-lg hover:shadow-xl"
            >
              View More Services
              <ArrowRight className="ml-2 h-6 w-6" />
            </Link>
          </motion.div>
        </div>
      </motion.section>

          {/* Features Section - Redesigned */}
          <section className="py-20 lg:py-28 bg-gradient-to-br from-white via-primary-50/30 to-secondary-50/30 relative overflow-hidden">
            {/* Decorative Background Elements */}
            <div className="absolute inset-0 overflow-hidden pointer-events-none">
              <div className="absolute top-20 left-10 w-72 h-72 bg-accent-200/20 rounded-full blur-3xl"></div>
              <div className="absolute bottom-20 right-10 w-96 h-96 bg-secondary-200/20 rounded-full blur-3xl"></div>
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-primary-200/10 rounded-full blur-3xl"></div>
            </div>

            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
              {/* Header Section */}
              <motion.div 
                className="text-center mb-16 lg:mb-20"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8 }}
                viewport={{ once: true }}
              >
                <motion.div
                  initial={{ opacity: 0, scale: 0.9 }}
                  whileInView={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.6, delay: 0.2 }}
                  viewport={{ once: true }}
                  className="inline-block mb-4"
                >
                  <span className="inline-flex items-center px-4 py-2 rounded-full bg-accent-100 text-accent-700 text-sm font-semibold">
                    <Award className="h-4 w-4 mr-2" />
                    Why Choose Us
                  </span>
                </motion.div>
                <h2 className="text-4xl sm:text-5xl lg:text-6xl font-bold text-gray-900 mb-6 leading-tight">
                  Why Choose <span className="text-primary-700">Taj Safety Nets?</span>
                </h2>
                <p className="text-lg sm:text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
                  We provide comprehensive invisible grill and cloth hanger solutions with unmatched quality and service - from balconies to windows, apartments, and modern living spaces across Hyderabad
                </p>
              </motion.div>

              {/* Features Grid - Single Row on Desktop */}
              <div className="grid grid-cols-2 md:grid-cols-2 lg:grid-cols-4 gap-4 sm:gap-6 lg:gap-8">
                {features.map((feature, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, y: 50 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6, delay: index * 0.15 }}
                    viewport={{ once: true }}
                    className="group relative"
                  >
                    {/* Card Container */}
                    <div className="relative h-full bg-white rounded-2xl p-4 sm:p-5 md:p-6 lg:p-8 shadow-lg hover:shadow-2xl transition-all duration-300 border border-gray-100 hover:border-primary-200 overflow-hidden">
                      {/* Gradient Overlay */}
                      <div className="absolute inset-0 bg-gradient-to-br from-primary-50/0 to-accent-50/0 group-hover:from-primary-50/30 group-hover:to-accent-50/20 transition-all duration-300"></div>
                      
                      <div className="relative z-10">
                        {/* Badge */}
                        <div className="inline-flex items-center mb-2 sm:mb-3 md:mb-4">
                          <span className="px-2 py-0.5 sm:px-3 sm:py-1 rounded-full bg-accent-100 text-accent-700 text-xs font-semibold">
                            Feature {index + 1}
                          </span>
                        </div>

                        {/* Title */}
                        <h3 className="text-base sm:text-lg md:text-xl lg:text-2xl font-bold text-gray-900 mb-2 sm:mb-3 group-hover:text-primary-700 transition-colors duration-300">
                          {feature.title}
                        </h3>

                        {/* Description */}
                        <p className="text-gray-600 leading-relaxed text-xs sm:text-sm lg:text-base">
                          {feature.description}
                        </p>
                      </div>

                      {/* Corner Accent */}
                      <div className="absolute top-0 right-0 w-24 h-24 bg-gradient-to-bl from-accent-100/20 to-transparent rounded-bl-3xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                    </div>
                  </motion.div>
                ))}
              </div>

              {/* Additional Trust Indicators */}
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.6 }}
                viewport={{ once: true }}
                className="mt-16 lg:mt-20 grid grid-cols-2 md:grid-cols-4 gap-6"
              >
                {[
                  { label: "25+ Years", sublabel: "Experience" },
                  { label: "5200+", sublabel: "Projects" },
                  { label: "5100+", sublabel: "Happy Clients" },
                  { label: "40+", sublabel: "Expert Staff" }
                ].map((stat, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, scale: 0.9 }}
                    whileInView={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 0.5, delay: 0.7 + index * 0.1 }}
                    viewport={{ once: true }}
                    className="text-center p-6 bg-white/80 backdrop-blur-sm rounded-xl border border-gray-100 shadow-md hover:shadow-lg transition-all duration-300 hover:border-primary-200"
                  >
                    <div className="text-3xl lg:text-4xl font-bold text-primary-700 mb-2">{stat.label}</div>
                    <div className="text-sm text-gray-600 font-medium">{stat.sublabel}</div>
                  </motion.div>
                ))}
              </motion.div>
            </div>
          </section>

          {/* Stats Section - Redesigned */}
          <section className="py-20 lg:py-28 bg-gradient-to-br from-primary-800 via-primary-900 to-primary-800 relative overflow-hidden">
            {/* Decorative Background Elements */}
            <div className="absolute inset-0 overflow-hidden pointer-events-none">
              <div className="absolute top-0 left-0 w-96 h-96 bg-accent-500/10 rounded-full blur-3xl"></div>
              <div className="absolute bottom-0 right-0 w-96 h-96 bg-secondary-500/10 rounded-full blur-3xl"></div>
            </div>

            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
              {/* Section Header */}
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8 }}
                viewport={{ once: true }}
                className="text-center mb-12 lg:mb-16"
              >
                <h2 className="text-3xl sm:text-4xl lg:text-5xl font-bold text-white mb-4">
                  Our Achievements
                </h2>
                <p className="text-lg text-gray-300 max-w-2xl mx-auto">
                  Trusted by thousands of satisfied customers across Hyderabad
                </p>
              </motion.div>

              {/* Stats Grid */}
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 sm:gap-6 lg:gap-8">
                {stats.map((stat, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, y: 50 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6, delay: index * 0.15 }}
                    viewport={{ once: true }}
                    className="group relative"
                  >
                    {/* Card Container */}
                    <div className="relative h-full bg-white/10 backdrop-blur-md rounded-2xl p-6 sm:p-8 border border-white/20 hover:border-accent-400/50 transition-all duration-300 hover:bg-white/15 hover:shadow-2xl hover:shadow-accent-500/20 overflow-hidden">
                      {/* Gradient Overlay on Hover */}
                      <div className="absolute inset-0 bg-gradient-to-br from-accent-500/0 to-secondary-500/0 group-hover:from-accent-500/20 group-hover:to-secondary-500/10 transition-all duration-300"></div>
                      
                      {/* Animated Background Pattern */}
                      <div className="absolute inset-0 opacity-5">
                        <div className="absolute top-4 right-4 w-20 h-20 border-2 border-white rounded-full"></div>
                        <div className="absolute bottom-4 left-4 w-12 h-12 border-2 border-white rounded-full"></div>
                      </div>

                      <div className="relative z-10 text-center">
                        {/* Icon Container */}
                        <motion.div
                          whileHover={{ scale: 1.1, rotate: 5 }}
                          transition={{ duration: 0.3 }}
                          className="inline-flex items-center justify-center w-16 h-16 sm:w-20 sm:h-20 rounded-2xl bg-gradient-to-br from-accent-500/20 to-secondary-500/20 mb-4 sm:mb-6 group-hover:from-accent-500/30 group-hover:to-secondary-500/30 transition-all duration-300 shadow-lg"
                        >
                          <div className="text-white group-hover:text-accent-300 transition-colors duration-300">
                            {stat.icon}
                          </div>
                        </motion.div>

                        {/* Number */}
                        <motion.div
                          initial={{ scale: 0 }}
                          whileInView={{ scale: 1 }}
                          transition={{ duration: 0.5, delay: 0.3 + index * 0.1, type: "spring" }}
                          viewport={{ once: true }}
                          className="text-4xl sm:text-5xl lg:text-6xl font-bold text-white mb-2 sm:mb-3 bg-gradient-to-r from-white via-accent-100 to-white bg-clip-text text-transparent group-hover:from-accent-300 group-hover:via-white group-hover:to-accent-300 transition-all duration-300"
                        >
                          {stat.number}
                        </motion.div>

                        {/* Label */}
                        <div className="text-sm sm:text-base lg:text-lg text-gray-300 font-medium group-hover:text-white transition-colors duration-300">
                          {stat.label}
                        </div>

                        {/* Decorative Line */}
                        <div className="mt-4 flex justify-center">
                          <div className="h-1 w-16 bg-gradient-to-r from-transparent via-accent-400 to-transparent rounded-full opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                        </div>
                      </div>

                      {/* Corner Accent */}
                      <div className="absolute top-0 right-0 w-24 h-24 bg-gradient-to-bl from-accent-400/20 to-transparent rounded-bl-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                    </div>
                  </motion.div>
                ))}
              </div>
            </div>
          </section>

      {/* CTA Section */}
      <section className="py-20 bg-gradient-to-br from-primary-900 via-primary-800 to-primary-900 text-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-3xl lg:text-4xl font-bold mb-6">
            Ready to Ensure Safety?
          </h2>
          <p className="text-xl text-gray-300 mb-8 max-w-2xl mx-auto">
            We are proud to serve customers across Hyderabad who have embraced the benefits of safety nets. Join the growing community that has already secured their premises with our protection solutions. Contact us today for a complimentary safety consultation on construction safety nets, cricket nets, gardening protection, and customized safety solutions.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
                <a
                  href="tel:+917893987771"
                  className="bg-gradient-to-r from-accent-500 to-accent-600 hover:from-accent-600 hover:to-accent-700 text-white px-8 py-4 rounded-lg font-semibold text-lg transition-colors shadow-lg"
                >
              Call Now: +91 7893987771
            </a>
            <button
              onClick={openWhatsAppQuote}
              className="bg-gradient-to-r from-secondary-500 to-secondary-600 hover:from-secondary-600 hover:to-secondary-700 text-white px-8 py-4 rounded-lg font-semibold text-lg transition-colors cursor-pointer shadow-lg"
            >
              Request Quote
            </button>
            <Link
              to="/services"
              className="bg-secondary-600 hover:bg-secondary-700 text-white px-8 py-4 rounded-lg font-semibold text-lg transition-colors border-2 border-secondary-600"
            >
              <Shield className="inline mr-2 h-6 w-6" />
              View Our Services
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
